import React, { useState, useEffect, useCallback } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import { API_URL } from '../api/config';
import { useAuth } from '../context/AuthContext';
import './Dashboard.css';

const CATEGORIES = [
  { value: 'food', label: 'Food' },
  { value: 'transportation', label: 'Transportation' },
  { value: 'entertainment', label: 'Entertainment' },
  { value: 'utilities', label: 'Utilities' },
  { value: 'shopping', label: 'Shopping' },
  { value: 'other', label: 'Other' }
];

function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newTransaction, setNewTransaction] = useState({
    description: '',
    amount: '',
    category: '',
    type: 'expense'
  });
  const [lastAddedTransaction, setLastAddedTransaction] = useState(null);
  const [wsConnection, setWsConnection] = useState(null);

  const history = useHistory();
  const { logout } = useAuth();

  const handleAuthError = useCallback(() => {
    logout();
    history.push('/login');
  }, [logout, history]);

  const fetchTransactions = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        handleAuthError();
        return;
      }

      const response = await axios.get(`${API_URL}/transactions`, {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setTransactions(response.data);
      setLoading(false);
      setError(null);
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 401) {
        handleAuthError();
      } else {
        setError('Failed to fetch transactions');
        setLoading(false);
      }
    }
  }, [handleAuthError]);

  useEffect(() => {
    fetchTransactions();
  }, [fetchTransactions]);

  // Initialize WebSocket connection
  useEffect(() => {
    const ws = new WebSocket('ws://localhost:3001/ws'); // Match your backend port

    ws.onopen = () => {
      console.log('WebSocket Connected');
      setWsConnection(ws);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('WebSocket message received:', data);
      // Handle incoming WebSocket messages
      if (data.type === 'transaction_update') {
        fetchTransactions();
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    ws.onclose = () => {
      console.log('WebSocket disconnected');
    };

    return () => {
      if (ws) {
        ws.close();
      }
    };
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        handleAuthError();
        return;
      }

      // Validate the data before sending
      if (!newTransaction.description.trim()) {
        setError('Description is required');
        return;
      }

      const numAmount = parseFloat(newTransaction.amount);
      if (isNaN(numAmount) || numAmount <= 0) {
        setError('Please enter a valid positive amount');
        return;
      }

      if (!newTransaction.category) {
        setError('Category is required');
        return;
      }

      const transactionData = {
        description: newTransaction.description.trim(),
        amount: numAmount,
        category: newTransaction.category,
        type: newTransaction.type || 'expense'
      };

      const response = await axios.post(
        `${API_URL}/transactions`,
        transactionData,
        {
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data) {
        setLastAddedTransaction({
          ...transactionData,
          id: response.data.id,
          date: new Date()
        });

        // Reset form
        setNewTransaction({
          description: '',
          amount: '',
          category: '',
          type: 'expense'
        });

        setError(null);
        fetchTransactions();
      }
    } catch (error) {
      if (error.response?.status === 403 || error.response?.status === 401) {
        handleAuthError();
      } else {
        setError('Failed to add transaction. Please try again.');
      }
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>Financial Dashboard</h2>
      </div>

      <div className="add-transaction">
        <h3>Add New Transaction</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              name="description"
              value={newTransaction.description}
              onChange={handleInputChange}
              placeholder="Description"
              required
            />
          </div>
          <div className="form-group">
            <input
              type="number"
              name="amount"
              value={newTransaction.amount}
              onChange={handleInputChange}
              placeholder="Amount"
              required
            />
          </div>
          <div className="form-group">
            <select
              name="category"
              value={newTransaction.category}
              onChange={handleInputChange}
              required
            >
              <option value="">Select Category</option>
              {CATEGORIES.map(cat => (
                <option key={cat.value} value={cat.value}>
                  {cat.label}
                </option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-primary">
            Add Transaction
          </button>
        </form>
      </div>

      {lastAddedTransaction && (
        <div className="transaction-summary">
          <h4>Last Added Transaction</h4>
          <div className="summary-content">
            <p><strong>Description:</strong> {lastAddedTransaction.description}</p>
            <p><strong>Amount:</strong> ${lastAddedTransaction.amount}</p>
            <p><strong>Category:</strong> {lastAddedTransaction.category}</p>
            <p><strong>Date:</strong> {new Date().toLocaleDateString()}</p>
          </div>
        </div>
      )}

      <div className="transactions-list">
        <h3>Recent Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Description</th>
              <th>Category</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <tr key={transaction.id}>
                <td>{new Date(transaction.date).toLocaleDateString()}</td>
                <td>{transaction.description}</td>
                <td>{transaction.category}</td>
                <td>${transaction.amount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
