import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_URL } from '../api/config';
import './Dashboard.css';

const CATEGORIES = [
  { value: 'income', label: 'Income' },
  { value: 'expense', label: 'Expense' },
  { value: 'food', label: 'Food & Dining' },
  { value: 'transport', label: 'Transportation' },
  { value: 'utilities', label: 'Utilities' },
  { value: 'entertainment', label: 'Entertainment' },
  { value: 'shopping', label: 'Shopping' },
  { value: 'other', label: 'Other' }
];

function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newTransaction, setNewTransaction] = useState({
    description: '',
    amount: '',
    category: '',
    type: 'expense'
  });
  const [lastAddedTransaction, setLastAddedTransaction] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('No token found in localStorage');
      // navigate('/login');
      return;
    }
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setError('No authentication token found');
        // Redirect to login if needed
        return;
      }

      const response = await axios.get(`${API_URL}/transactions`, {
        headers: { 
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setTransactions(response.data);
      setLoading(false);
      setError(null);
    } catch (error) {
      const errorMessage = error.response?.status === 403 
        ? 'Please login again to continue'
        : 'Failed to fetch transactions';

      setError(errorMessage);
      setLoading(false);

      // If token is invalid, redirect to login
      if (error.response?.status === 403) {
        localStorage.removeItem('token');
        // Add your navigation logic here, e.g.:
        // navigate('/login');
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setError('No authentication token found');
        return;
      }

      // Validate the data before sending
      if (!newTransaction.description.trim()) {
        setError('Description is required');
        return;
      }

      const numAmount = parseFloat(newTransaction.amount);
      if (isNaN(numAmount) || numAmount <= 0) {
        setError('Please enter a valid positive amount');
        return;
      }

      if (!newTransaction.category) {
        setError('Category is required');
        return;
      }

      // Prepare the data in the exact format the backend expects
      const transactionData = {
        description: newTransaction.description.trim(),
        amount: numAmount,
        category: newTransaction.category,
        type: newTransaction.type || 'expense'
      };

      console.log('Sending transaction data:', transactionData); // Debug log

      const response = await axios.post(
        `${API_URL}/transactions`,
        transactionData,
        {
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data) {
        setLastAddedTransaction({
          ...transactionData,
          id: response.data.id,
          date: new Date()
        });

        // Reset form
        setNewTransaction({
          description: '',
          amount: '',
          category: '',
          type: 'expense'
        });

        setError(null);
        fetchTransactions();
      }
    } catch (error) {
      console.error('Transaction error:', error.response?.data || error.message); // Debug log

      const errorMessage = error.response?.data?.message || 
                          error.response?.status === 403 
                            ? 'Please login again to continue'
                            : 'Failed to add transaction';

      setError(errorMessage);

      if (error.response?.status === 403) {
        localStorage.removeItem('token');
        // navigate('/login');
      }
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>Financial Dashboard</h2>
      </div>

      <div className="add-transaction">
        <h3>Add New Transaction</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              name="description"
              value={newTransaction.description}
              onChange={handleInputChange}
              placeholder="Description"
              required
            />
          </div>
          <div className="form-group">
            <input
              type="number"
              name="amount"
              value={newTransaction.amount}
              onChange={handleInputChange}
              placeholder="Amount"
              required
            />
          </div>
          <div className="form-group">
            <select
              name="category"
              value={newTransaction.category}
              onChange={handleInputChange}
              required
            >
              <option value="">Select Category</option>
              {CATEGORIES.map(cat => (
                <option key={cat.value} value={cat.value}>
                  {cat.label}
                </option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-primary">
            Add Transaction
          </button>
        </form>
      </div>

      {lastAddedTransaction && (
        <div className="transaction-summary">
          <h4>Last Added Transaction</h4>
          <div className="summary-content">
            <p><strong>Description:</strong> {lastAddedTransaction.description}</p>
            <p><strong>Amount:</strong> ${lastAddedTransaction.amount}</p>
            <p><strong>Category:</strong> {lastAddedTransaction.category}</p>
            <p><strong>Date:</strong> {new Date().toLocaleDateString()}</p>
          </div>
        </div>
      )}

      <div className="transactions-list">
        <h3>Recent Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Description</th>
              <th>Category</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <tr key={transaction.id}>
                <td>{new Date(transaction.date).toLocaleDateString()}</td>
                <td>{transaction.description}</td>
                <td>{transaction.category}</td>
                <td>${transaction.amount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
